"use strict";

var buildings_menu = document.querySelectorAll(".header__menu_item_button");

if (buildings_menu) {
  (function () {
    var e = document.querySelectorAll(".header__menu_buildings");

    var _loop = function _loop(n) {
      buildings_menu[n].onmouseover = function () {
        e[n].removeAttribute("hidden");
      }, buildings_menu[n].onmouseout = function () {
        e[n].setAttribute("hidden", !0);
      };
    };

    for (var n = 0; n < buildings_menu.length; n++) {
      _loop(n);
    }
  })();
}

var form = document.querySelector(".main__slide1_form");
form && (form__button.onform = function () {
  document.querySelector(".main__slide1_form_phone"), fetch("send.php", {
    method: "POST",
    body: new FormData(form)
  });
  return !1;
});